import React, {useState} from "react";
export type FilterValueType= 'All' | 'Active' | 'Completed'
export type tasks1Type={
    id: string
    title:string
    isDone: boolean
}
export type TodoListType={
    title:string
    tasks:Array <tasks1Type>
    removeTasks:(id:string)=>void
    chengFilter: (value:FilterValueType)=>void
    addTask:(title:string)=>void
}

export const TodoList = (props:TodoListType) => {
    const [newTitle, setNewTitle]=useState('')
    return (
        <div>
            <h3>{props.title}</h3>
            <div>
                <input value={newTitle}
                       onChange={(e)=>{setNewTitle(e.currentTarget.value)}}/>
                <button onClick={()=>{props.addTask(newTitle)}}>+</button>
                {setNewTitle('')}
            </div>
            <ul>
                {props.tasks.map((t)=><li><input type="checkbox" checked={t.isDone}/>
                    <span>{t.title}</span>
                    <button onClick={()=> { props.removeTasks(t.id)   }}>x</button>
                </li>
                )}


            </ul>
            <div>
                <button onClick={()=>props.chengFilter('All')}    >All</button>
                <button onClick={()=> props.chengFilter('Active')}>Active</button>
                <button onClick={()=> props.chengFilter('Completed')}>Completed</button>
            </div>
        </div>
    )
}
